import React, {useState} from 'react';
import './ContactForm.css'
import Button from "../UI/Button/Button";

const initialValues = {
    name: '',
    email: '',
    phone: '',
    message: ''
}
const initialErrors = {
    name: '',
    email: '',
    phone: '',
    message: ''
};

function ContactForm(props) {
    const [formValues, setFormValues] = useState(initialValues);
    const [errors, setErrors] = useState(initialErrors);
    const changeNameHandler = (event) => {
        if (event.target.value.trim().length === 0) {
            setErrors((prevState) => {
                return {
                    ...prevState,
                    name: 'required'
                }
            })
        }

        setFormValues((prevState) => {
            return {
                ...prevState,
                name: event.target.value
            }
        })
    }
    return (
        <form>
            <div className={'contact-us__controls'}>
                <div className={'contact-us__user_details'}>
                    <div className={'contact-us__control'}>
                        <label>Name</label>
                        <input onChange={changeNameHandler} value={formValues.name}/>
                        {errors.name.length != 0 && <div className={'error'}>
                            <p>
                                {errors.name}
                            </p>
                        </div>}

                    </div>
                    <div className={'contact-us__control'}>
                        <label>Name</label>
                        <input/>
                        <div className={'error'}>
                            <p>

                            </p>
                        </div>
                    </div>
                    <div className={'contact-us__control'}>
                        <label>Name</label>
                        <input/>
                        <div className={'error'}>
                            <p>
                                asdasd
                            </p>
                        </div>
                    </div>
                </div>
                <div className={'contact-us__user_message'}>

                </div>
            </div>
        </form>
    );
}

export default ContactForm;
