import React from "react";
import { GridItem } from "../UI/Grid/GridItem";
import styles from "./Project.module.css";
const Project = (props) => {
  const showProjectDetailsHandler = () => {
    ReactDOM.createPortal('model-root')
  };
  return (
    <GridItem className={styles["project"]}>
      <div
        className={styles["project__img"]}
        onClick={showProjectDetailsHandler}
      >
        <div className={styles["project__img-hover"]}>
          <span>+</span>
        </div>
        <img
          src={require(`../../assests/images/projects/${props.order}.jpg`)}
          alt="project"
        />
      </div>
      <div className={styles["project__details"]}>
        <h4>{props.title}</h4>
        <p>{props.details}</p>
      </div>
    </GridItem>
  );
};
export default Project;
